/**
 * General settings form.
 */
function $module$_settings_form($form, &$form_state) {
  $form = array();
  $form['$module$_$setting1$'] = array(
    '#type'          => 'textfield',
    '#required'      => TRUE,
    '#title'         => t('$title$'),
    '#description'   => t('$description$'),
    '#default_value' => variable_get('$module$_$setting1$'),
  );
  return system_settings_form($form);
  /*
   * MENU CALLBACK ITEMS. COPY REQUIRED ITEMS TO HOOK MENU.
   */
  /*
   * In order to generate an item on the configuration page, you need three menu items.
   * 1. The first is the higher level item, which causes an item to appear on the
   *    settings page.
   * 2. The second is the actual menu item for the settings.
   * 3. Is the optional first tab. You need more of these items
   *    for more tabs.
   */
  $items['admin/config/$module$'] = array(
    'title'            => '$menu_blocktitle$',
    'position'         => 'right',
    'weight'           => -10,
    'page callback'    => 'system_admin_menu_block_page',
    'access arguments' => array('$access_arguments$'),
    'file'             => 'system.admin.inc',
    'file path'        => drupal_get_path('module', 'system'),
  );
  $items['admin/config/$module$/settings'] = array(
    'title'            => '$menu_title$',
    'description'      => '$menu_description$',
    'access arguments' => array('$access_arguments$'),
    'page callback'    => 'drupal_get_form',
    'page arguments'   => array('$module$_settings_form'),
    'weight'           => -10,
  );
  // Optional if using tabs. Delete when not necessary.
  $items['admin/config/$module$/settings/general'] = array(
    'title'            => '$menu_title$',
    'description'      => '$menu_description$',
    'access arguments' => array('$access_arguments$'),
    'type'             => MENU_DEFAULT_LOCAL_TASK,
    'weight'           => -10,
  );
  /*
   * END OF MENU ITEMS.
   */
}

/**
 * Get a setting.
 *
 * The settings can be stored in a variable, but another option is also possible.
 *
 * @param string $setting
 *
 * @return mixed
 *   Variable or FALSE.
 */
function $module$_get_setting($setting = '$module$_$setting1$') {
  return variable_get($setting, FALSE);
}

